import React, {Component} from "react"; // "react"라이브러리에서 React와 Component 클래스를 가져온다.
import "./App.css";

export default class App extends Component {
  btnStyle = { // 스타일(정적으로 지정하여 사용 가능)
    color: "#fff",
    border: "none",
    padding: "5px 9px",
    borderRadius: "50%",
    cursor: "pointer",
    float: "right"
  }

  getStyle = () => { // 스타일 함수(동적으로 사용 가능)
    return {
      padding: "10px",
      borderBottom: "1px #ccc dotted",
      textDecoration: "none"
    }
  }

  todoData = [
    {
      id: "1",
      title: "공부하기",
      completed: true
    },
    {
      id: "2",
      title: "청소하기",
      completed: false
    }
  ]

  handleClick = (id) => {
    let newTodoData = this.todoData.filter(data => data.id !== id)
    // console.log('newTodoData: ${newTodoData}');
  }

  render() { // render(): Component의 함수
    return(
      <div className="container">
        <div className="todoBlock">
          <div class="title">
            <h1>할 일 목록</h1>
          </div>

          {this.todoData.map((data) => (
            <div style={this.getStyle()} key={data.id}>
            <input type="checkbox" defaultChecked={false} />
              {data.title}
            <button style={this.btnStyle} onClick={() => this.handleClick(data.id)}>x</button>
          </div>
          ))}

        </div>
      </div>
    );
  }
}
