import React, { useState } from 'react';

const List = React.memo(({
    id, title, completed, todoData, setTodoData, provided, snapshot, handleClick
}) => {
    const [isEditing, setIsEditing] = useState(false);
    const [editedTitle, setEditedTitle] = useState(title);

    const handleCompleteChange = (id) => {
        let newTodoData = todoData.map((data) => {
            if (data.id === id) {
                data.completed = !completed;
            }
            return data;
        })
        // this.setState({ todoData : newTodoData });
        setTodoData(newTodoData);
    }

    const handleEditChange = (e) => {
        setEditedTitle(e.target.value);
    }

    const handleSubmit = (e) => {

    }

    if (isEditing) {
        return (
            <div
                className='flex item-center justify-between w-full px-4 py-1 my-2 text-gray-600 bg-gray-100 border rounded'>
                <div className='items-center py-2'>
                    <form onSubmit={handleSubmit}>
                        <input
                            className="w-full px-3 py-2 mr-4 text-gray-500 rounded"
                            value={editedTitle}
                            onChange={handleEditChange}
                        />
                    </form>
                </div>
                <div className='items-center'>
                    <button
                        className='px-4 py-2 float-right'
                        onClick={() => setIsEditing(false)}>x
                    </button>
                    <button
                        onClick={handleSubmit}
                        className='px-4 py-2 float-right'
                        type="submit">save
                    </button>
                </div>

            </div>
        );
    } else {
        return (
            <div
                key={id}
                {...provided.draggableProps}
                ref={provided.innerRef}
                {...provided.dragHandleProps}
                className={`${snapshot.isDragging ? "bg-blue-400" : "bg-gray-100"} flex item-center justify-between w-full px-4 py-1 my-2 text-gray-600 bg-gray-100 border rounded`}>

                <div className='items-center py-2'>
                    <input
                        type="checkbox"
                        defaultChecked={false}
                        onChange={() => handleCompleteChange(id)}
                    />{" "}
                    <span
                        className={completed ? "line-through" : undefined}>
                        {title}
                    </span>
                </div>
                <div className='items-center'>
                    <button
                        className='px-4 py-2 float-right'
                        onClick={() => handleClick(id)}>x
                    </button>
                    <button
                        className='px-4 py-2 float-right'
                        onClick={() => setIsEditing(true)}>edit
                    </button>
                </div>

            </div>
        )
    }
})

export default List;
